```typescript
// 良い例 - メモ化。
function memoize<T, R>(fn: (arg: T) => R): (arg: T) => R {
  const cache = new Map<T, R>();。

  return (arg: T): R => {
    if (cache.has(arg)) {。
      return cache.get(arg)!;。
    }

    const result = fn(arg);。
    cache.set(arg, result);。
    return result;。
  };
}

const calculateExpensiveValue = memoize((input: number) => {
  // 計算コストの高い処理。
  return input * input;。
});

// 避けるべき例 - 不必要なオブジェクト生成。
